  <script>
    // Fade-in animation for elements
    const fadeInOnScroll = () => {
      const elements = document.querySelectorAll('.category-item');
      const windowHeight = window.innerHeight;

      elements.forEach((element) => {
        const position = element.getBoundingClientRect().top;
        if (position < windowHeight - 100) {
          element.style.opacity = "1";
          element.style.transform = "translateY(0)";
        }
      });
    };

    // Scroll to top button visibility
    const scrollToTopButton = document.getElementById('scrollToTop');
    
    window.addEventListener('scroll', () => {
      if (window.scrollY > 300) {
        scrollToTopButton.style.display = 'block';
      } else {
        scrollToTopButton.style.display = 'none';
      }
    });

    // Scroll to top functionality
    scrollToTopButton.addEventListener('click', () => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    });

    // Initial fade-in effect when the page loads
    document.addEventListener('DOMContentLoaded', () => {
      const heroContent = document.querySelectorAll('.category-item');
      heroContent.forEach((item) => {
        item.style.opacity = "1";
        item.style.transform = "translateY(0)";
      });
    });

    // Trigger fade-in on scroll
    document.addEventListener('scroll', fadeInOnScroll);
  </script>
