<script>
    // Function to handle adding items to the cart
    document.querySelectorAll('.btn-add-to-cart').forEach(button => {
        button.addEventListener('click', (event) => {
            const product = event.target.closest('.product-item'); // Get the product item
            const productName = product.querySelector('h3').innerText; // Get product name
            const productPrice = product.querySelector('p').innerText; // Get product price
           
            
            // Create the product object
            const cartItem = {
                name: productName,
                price: productPrice,
                
            };
            
            // Get current cart from local storage, or create an empty array if not present
            let cart = JSON.parse(localStorage.getItem('cart')) || [];
            
            // Add the new item to the cart
            cart.push(cartItem);
            
            // Save the updated cart back to local storage
            localStorage.setItem('cart', JSON.stringify(cart));
    
            alert('Item added to cart!');
        });
    });
    </script>
    
